import React, { Component } from 'react';

class Weather extends Component {

    state = {
        loading: true,
        person: null,
        kelvin: 0,
        celcius: 0,
        farenheit: 0,
        finalvalue: 0,
        flagcelcius: true,

        cold: false,
        mild: false,
        sunny: false,

    };

    async componentDidMount() {
        const location = "Hyderabad";
        const url = "https://api.openweathermap.org/data/2.5/onecall?lat=17.3850&lon=78.4867&exclude=minutely,hourly,daily,alerts&appid=b16f3090c65c3fb6b47c6222d3fbcd60";
        const response = await fetch(url);
        const data = await response.json();
        this.setState({ person: data.current, loading: false });


        this.state.kelvin = this.state.person.temp;
        this.state.celcius = this.state.kelvin - 273.15;
        this.state.farenheit = (this.state.celcius * 9 / 5) + 32;
        this.state.finalvalue = this.state.flagcelcius ? this.state.celcius : this.state.farenheit;

    }





    render() {
        return (

            <div>
                <h1>WEATHER COMPONENT:</h1>
                {this.state.loading || !this.state.person ? (<div>loading...</div>)
                    :
                    (
                        <div>
                            <div>Temperature in
                            {this.state.flagcelcius && <label> Celcius: </label>}
                                {!this.state.flagcelcius && <label> Farenheit: </label>}
                                {this.state.finalvalue}
                            </div>
                            <div>Pressure: {this.state.person.pressure}</div>
                            <div>Humidity: {this.state.person.humidity}</div>
                            <div>Sky Condition: {this.state.person.weather[0].description} </div>


                            <br/>
                        </div>
                    )}


                <a className="refreshbutton" onClick={() => this.componentDidMount()}>REFRESH</a>
            </div>
            );
    }




}

export default Weather;
